import {useRef} from 'react';
import {Link, useNavigate} from "react-router-dom";
import {ErrorToast, IsEmail, IsEmpty, IsMobile} from "../../helper/FormHelper.js";
import {RegistrationRequest} from "../../APIRequest/UserAPIRequest.js";

const Registration = () => {

    const emailRef = useRef();
    const firstNameRef = useRef();
    const lastNameRef = useRef();
    const mobileRef = useRef();
    const passwordRef = useRef();

    const navigate = useNavigate();

    const onRegistration = async () => {
        let email = emailRef.current?.value;
        let firstName = firstNameRef.current?.value;
        let lastName = lastNameRef.current?.value;
        let mobile = mobileRef.current?.value;
        let password = passwordRef.current?.value;
        let photo = "data:image/png;base64,"

        // ✅ Improved Validations
        if (IsEmpty(firstName)) {
            ErrorToast("First Name Required!");
        } else if (IsEmpty(lastName)) {
            ErrorToast("Last Name Required!");
        } else if (IsEmpty(mobile)) {
            ErrorToast("Mobile Number Required!");
        } else if (!IsMobile(mobile)) {
            ErrorToast("Valid Mobile Number Required!");
        } else if (IsEmpty(email)) {
            ErrorToast("Email Required!");
        } else if (IsEmail(email)) {
            ErrorToast("Valid Email Address Required!");
        } else if (IsEmpty(password)) {
            ErrorToast("Password Required!");
        } else if (password.length < 6) {
            ErrorToast("Password must be at least 6 characters!");
        } else {
            const result = await RegistrationRequest(email, firstName, lastName, mobile, password, photo);
            if (result === true) {
                navigate("/login");
            }
        }
    };

    return (
        <div className="container">
            <div className="row justify-content-center">
                <div className="col-md-7 col-lg-6 center-screen">
                    <div className="card animated fadeIn w-100 px-5 mt-3">
                        <div className="card-body text-start">
                            <h3 className="text-center">SIGN UP</h3>
                            <br/>
                            <label>First Name</label>
                            <input ref={firstNameRef} placeholder="First Name"
                                   className="form-control animated fadeInUp" type="text"/>
                            <br/>
                            <label>Last Name</label>
                            <input ref={lastNameRef} placeholder="Last Name" className="form-control animated fadeInUp"
                                   type="text"/>
                            <br/>
                            <label>Mobile Number</label>
                            <input ref={mobileRef} placeholder="Mobile" className="form-control animated fadeInUp"
                                   type="tel"/>
                            <br/>
                            <label>Email Address</label>
                            <input ref={emailRef} placeholder="User Email" className="form-control animated fadeInUp"
                                   type="email"/>
                            <br/>
                            <label>Password</label>
                            <input ref={passwordRef} placeholder="User Password"
                                   className="form-control animated fadeInUp" type="password"/>
                            <br/>
                            <button onClick={onRegistration}
                                    className="btn btn-success w-100 animated ">
                                Next
                            </button>
                            <div className="text-center w-100">
                                <Link className="text-center ms-3 h6" to="/login">
                                    Sign In
                                </Link>
                                <br/>
                                <Link className="text-center ms-3 h6" to="/forget-password">
                                    Forgot Password?
                                </Link>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Registration;